import React, { useState } from 'react';

const TodoList = () => {
  const [todos, setTodos] = useState([
    { id: 1, title: 'Complete React Tutorial', completed: false, priority: 'High', category: 'Learning' },
    { id: 2, title: 'Review Project Feedback', completed: true, priority: 'Medium', category: 'Project' },
    { id: 3, title: 'Prepare for Team Meeting', completed: false, priority: 'High', category: 'Work' },
    { id: 4, title: 'Read Chapter 5 of Design Patterns', completed: false, priority: 'Low', category: 'Learning' }
  ]);
  
  const [newTodo, setNewTodo] = useState('');
  const [filter, setFilter] = useState('all');
  const [aiSuggestion, setAiSuggestion] = useState(null);
  
  const handleAddTodo = (e) => {
    e.preventDefault();
    if (!newTodo.trim()) return;
    
    const newTask = {
      id: Date.now(),
      title: newTodo,
      completed: false,
      priority: 'Medium',
      category: 'General'
    };
    
    setTodos([...todos, newTask]);
    setNewTodo('');
    
    // Simulate AI suggestion after adding a task
    setTimeout(() => {
      setAiSuggestion({
        message: "I noticed you're adding several learning tasks. Would you like to create a structured learning path?",
        action: "Create Learning Path"
      });
    }, 1000);
  };
  
  const toggleComplete = (id) => {
    setTodos(todos.map(todo => 
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    ));
  };
  
  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };
  
  const filteredTodos = todos.filter(todo => {
    if (filter === 'all') return true;
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });
  
  const dismissSuggestion = () => {
    setAiSuggestion(null);
  };

  return (
    <div className="space-y-6">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-4">Todo List</h1>
        
        {/* AI Suggestion */}
        {aiSuggestion && (
          <div className="mb-6 bg-indigo-50 dark:bg-indigo-900 border-l-4 border-indigo-500 p-4 rounded flex justify-between items-center">
            <div>
              <p className="text-indigo-700 dark:text-indigo-200">{aiSuggestion.message}</p>
            </div>
            <div className="flex space-x-2">
              <button className="px-3 py-1 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition">
                {aiSuggestion.action}
              </button>
              <button 
                onClick={dismissSuggestion}
                className="px-3 py-1 bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-200 rounded hover:bg-gray-300 dark:hover:bg-gray-600 transition"
              >
                Dismiss
              </button>
            </div>
          </div>
        )}
        
        {/* Add Todo Form */}
        <form onSubmit={handleAddTodo} className="mb-6">
          <div className="flex">
            <input
              type="text"
              value={newTodo}
              onChange={(e) => setNewTodo(e.target.value)}
              placeholder="Add a new task..."
              className="flex-grow px-4 py-2 border rounded-l focus:outline-none focus:ring-2 focus:ring-indigo-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white"
            />
            <button 
              type="submit"
              className="px-4 py-2 bg-indigo-600 text-white rounded-r hover:bg-indigo-700 transition"
            >
              Add
            </button>
          </div>
        </form>
        
        {/* Filters */}
        <div className="flex space-x-2 mb-6">
          <button 
            onClick={() => setFilter('all')}
            className={`px-3 py-1 rounded ${filter === 'all' ? 'bg-indigo-600 text-white' : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-200'}`}
          >
            All
          </button>
          <button 
            onClick={() => setFilter('active')}
            className={`px-3 py-1 rounded ${filter === 'active' ? 'bg-indigo-600 text-white' : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-200'}`}
          >
            Active
          </button>
          <button 
            onClick={() => setFilter('completed')}
            className={`px-3 py-1 rounded ${filter === 'completed' ? 'bg-indigo-600 text-white' : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-200'}`}
          >
            Completed
          </button>
        </div>
        
        {/* Todo List */}
        <div className="space-y-3">
          {filteredTodos.length === 0 ? (
            <p className="text-gray-500 dark:text-gray-400 text-center py-4">No tasks to display</p>
          ) : (
            filteredTodos.map(todo => (
              <div 
                key={todo.id} 
                className={`flex items-center justify-between p-4 rounded-lg ${
                  todo.completed ? 'bg-gray-100 dark:bg-gray-700 opacity-70' : 'bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700'
                }`}
              >
                <div className="flex items-center space-x-3">
                  <input
                    type="checkbox"
                    checked={todo.completed}
                    onChange={() => toggleComplete(todo.id)}
                    className="h-5 w-5 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
                  />
                  <div>
                    <p className={`font-medium ${todo.completed ? 'line-through text-gray-500 dark:text-gray-400' : 'text-gray-900 dark:text-white'}`}>
                      {todo.title}
                    </p>
                    <div className="flex space-x-2 mt-1">
                      <span className={`px-2 py-0.5 text-xs rounded ${
                        todo.priority === 'High' ? 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200' :
                        todo.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200' :
                        'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200'
                      }`}>
                        {todo.priority}
                      </span>
                      <span className="px-2 py-0.5 text-xs rounded bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200">
                        {todo.category}
                      </span>
                    </div>
                  </div>
                </div>
                <button
                  onClick={() => deleteTodo(todo.id)}
                  className="text-red-600 hover:text-red-800 dark:text-red-400 dark:hover:text-red-300"
                >
                  Delete
                </button>
              </div>
            ))
          )}
        </div>
      </div>
      
      {/* AI Optimization Section */}
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
        <h2 className="text-xl font-bold text-gray-900 dark:text-white mb-4">AI Task Optimization</h2>
        <p className="text-gray-600 dark:text-gray-300 mb-4">
          Let AI analyze your tasks and suggest improvements to your workflow.
        </p>
        <button className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition">
          Optimize My Tasks
        </button>
      </div>
    </div>
  );
};

export default TodoList;
